// This might be a little unconventionally structured, but it's meant to mirror
// the code generated by jst.ejs files.
(function() {
  this.JST || (this.JST = {});
  var jst = this.JST;
  // This shouldn't ever conflict with a template key, since those all start
  // with 'backbone'.
  this.JST.template = function(shortPath) {
    return jst['backbone/templates/' + shortPath];
  }
}).call(this);
